public static int longestValidParentheses(String s) {
    int max_length = 0;
    if (s == null || s.length() <= 1) return max_length;

    boolean[][] cache = new boolean[s.length()][s.length()];

    for (int i = 0; i < s.length() - 1; i++) {
        if (s.charAt(i) == '(' && s.charAt(i + 1) == ')') {
            cache[i][i + 1] = true;
            max_length = 2;
        }
    }

    for (int k = 4; k < s.length(); k += 2) {
        for (int i = 0; i <= s.length() - k; i++) {
            int j = i + k - 1;
            if (s.charAt(i) == '(' && s.charAt(j) == ')') {
                boolean sub = false;
                if (cache[i + 1][j - 1]) {
                    sub = true;
                } else {
                    for (int l = 2; l < k - 2; l += 2) {
                        if (cache[i + 1][i + l] && cache[i + l + 1][j - 1]) {
                            sub = true;
                            break;
                        }
                    }
                }
                if (sub) {
                    cache[i][j] = true;
                    max_length = k;
                }
            }
        }
    }

    return max_length;
}
